修改MAX_LEN不失为一种有效率的方法

    public static List<String> splitLog(String msg) {
        List<String> list = new ArrayList<>();
        int index = 0;
        int step = 1;
        while (index < msg.length()) {
            int stepTotal = step * 500;
            boolean isEnd = false;
            if (stepTotal + index > msg.length()) {
                stepTotal = msg.length() - index;
                isEnd = true;
            }
            if (msg.substring(index, index + stepTotal).getBytes().length > MAX_LEN || isEnd && step == 1) {
                if (isEnd && step == 1) {
                    list.add(msg.substring(index, index + stepTotal));
                    index = index + stepTotal;
                } else {
                    step--;
                    list.add(msg.substring(index, index + step * 500));
                    index = index + step * 500;
                }

                step = 1;
            } else {
                step++;
            }

        }
        return list;
    }

