        {
            if (object.Equals((object)aLeft, (object)null))
                return object.Equals((object)aRight, (object)null);
            else
                return aLeft.Equals((object)aRight);
        }

        public static bool operator !=(EmailActivityEntity<TPrimaryKey> aLeft, EmailActivityEntity<TPrimaryKey> aRight)
        {
            return !(aLeft == aRight);
        }


        public override bool Equals(object obj)
        {
            if (obj == null || !(obj is Entity<TPrimaryKey>))
                return false;
            if (object.ReferenceEquals((object)this, obj))
                return true;
            Entity<TPrimaryKey> entity = (Entity<TPrimaryKey>)obj;
            if (this.IsTransient() && entity.IsTransient())
                return false;
            Type type1 = this.GetType();
            Type type2 = entity.GetType();
            if (!type1.IsAssignableFrom(type2) && !type2.IsAssignableFrom(type1))
                return false;
            else
                return this.Id.Equals((object)entity.Id);
        }

        public override int GetHashCode()
        {
            return this.Id.GetHashCode();
        }

        public override string ToString()
        {
            return string.Format("[{0} {1}]", (object)this.GetType().Name, (object)this.Id);
        }


