public Task<SiloAddress> OnAddActivation(PlacementStrategy strategy, PlacementTarget target,
            IPlacementContext context)
{
	var allSilos = context.GetCompatibleSilos(target).OrderBy(s => s).ToArray();
	var key = GetTargetPrimaryKey(target);
	var hash = key.GetHashCode() & 0x7fffffff;

	var index = hash % allSilos.Length;
	return Task.FromResult(allSilos[index]);
}

private static string GetTargetPrimaryKey(PlacementTarget target)
{
	var key = target.GrainIdentity.PrimaryKeyString;
	var result = key;
	if (key.Contains("__"))
	{
		result = key[..key.IndexOf("__", StringComparison.Ordinal)].Trim();
	}

	return result;
}
