    {
        Task<DiscoveryResponse> GetDiscovered();
    }

    [StorageProvider(ProviderName = "AzureTable")]
    public class DiscoveryGrain : Grain<DiscoveryResponse>, IDiscoveryGrain, IRemindable
    {
        public override async Task OnDeactivateAsync()
        {
            await RegisterOrUpdateReminder(
                reminderName: "InactiveCacheRefresh", 
                dueTime: TimeSpan.FromHours(1), 
                period: TimeSpan.Zero);
        }

        async Task IRemindable.ReceiveReminder(string reminderName, TickStatus status)
        {
            await RefreshCache(null);
            DeactivateOnIdle(); // So the grain's would be immedietly deactivated and GC could collect its memory  
        }

        public Task<DiscoveryResponse> GetDiscovered()
        {
            if (_activeCacheRefresh == null)
            {
                _activeCacheRefresh = RegisterTimer(
                    asyncCallback: RefreshCache,
                    state: null,
                    dueTime: TimeSpan.Zero,
                    period: TimeSpan.FromMinutes(1));
            }

            return Task.FromResult(_cachedResponse);
        }

        private IDisposable _activeCacheRefresh;

        #region Helpers
        private async Task RefreshCache(object ignored)
        {
            var subscriptionId = this.GetPrimaryKey();
            var refreshedResponse = await DiscoverResources(subscriptionId);

            _cachedResponse = refreshedResponse;
            await WriteStateAsync();
        }

        private DiscoveryResponse _cachedResponse;

        private Task<DiscoveryResponse> DiscoverResources(Guid subscriptionId) { throw new NotImplementedException(); }

        private void AfterRefresh(Task refreshTask) { throw new NotImplementedException(); } 
        #endregion
    }
```

