using static Microsoft.CodeAnalysis.CSharp.SyntaxFactory;

namespace Test.CodeFixes
{
    [TestClass]
    public class UnitTest1
    {
        internal static readonly MetadataReference CorlibReference = MetadataReference.CreateFromFile(typeof(object).Assembly.Location);
        internal static readonly MetadataReference SystemCoreReference = MetadataReference.CreateFromFile(typeof(Enumerable).Assembly.Location);
        internal static readonly MetadataReference CSharpSymbolsReference = MetadataReference.CreateFromFile(typeof(CSharpCompilation).Assembly.Location);
        internal static readonly MetadataReference CodeAnalysisReference = MetadataReference.CreateFromFile(typeof(Compilation).Assembly.Location);

        [TestMethod]
        public void TestCompilationError()
        {
            var testCode = @"

using System.ComponentModel.Composition;

[System.ComponentModel.Composition.Export(typeof(System.String))]
[Export(typeof(System.Single))]
public class TempClass
{
}";

            var project = CreateProject(testCode);

            var document = project.Documents.Single();
            CheckForCompilationErrors(project.GetCompilationAsync().Result);

            var newDocument = AddAttributeAsyncFix(document, document.GetSyntaxRootAsync().Result.DescendantNodes().OfType<ClassDeclarationSyntax>().Single()).Result;
            Console.WriteLine(@"new code:
" + newDocument.GetTextAsync().Result);

            CheckForCompilationErrors(newDocument.Project.GetCompilationAsync().Result);
        }

        private Project CreateProject(string testCode)
        {
            var TestProjectName = "TestProject";
            var projectId = ProjectId.CreateNewId(TestProjectName);

            var solution = new AdhocWorkspace()
                .CurrentSolution
                .AddProject(projectId, TestProjectName, TestProjectName, LanguageNames.CSharp)
                .WithProjectCompilationOptions(projectId, new CSharpCompilationOptions(OutputKind.DynamicallyLinkedLibrary))
                .AddMetadataReference(projectId, CorlibReference)
                .AddMetadataReference(projectId, SystemCoreReference)
                .AddMetadataReference(projectId, CSharpSymbolsReference)
                .AddMetadataReference(projectId, CodeAnalysisReference)
                .AddMetadataReferences(projectId, GetAdditionalReferences());


            var documentId = DocumentId.CreateNewId(projectId);
            solution = solution.AddDocument(documentId, "test.cs", SourceText.From(testCode));

            var project = solution.GetProject(projectId);
            return project;
        }

        private async Task<Document> AddAttributeAsyncFix(
            Document document,
            ClassDeclarationSyntax classDeclaration)
        {
            //Add [Export(typeof(TypeName))]
            var root = await document.GetSyntaxRootAsync();

            var typeofArgument = AttributeArgument(TypeOfExpression(ParseTypeName("System.Double")));
            var attributeSyntax = Attribute(IdentifierName("System.ComponentModel.Composition.Export"), AttributeArgumentList().AddArguments(typeofArgument));
            var attributeListSyntax = AttributeList(SeparatedList(new[] { attributeSyntax }));
            var newClass = classDeclaration.AddAttributeLists(attributeListSyntax);
            root = root.ReplaceNode(classDeclaration, newClass);

            return document.WithSyntaxRoot(root);
        }

        private static void CheckForCompilationErrors(Compilation compilation)
        {
            var compilationDiagnositics = compilation.GetDiagnostics();
            var errorDiagnostics = compilationDiagnositics.Where(x => x.Severity == DiagnosticSeverity.Error);
            if (errorDiagnostics.Any())
            {
                Assert.Fail($@"There are errors in the code to be analyzed. See below:
{string.Join("\r\n", errorDiagnostics.Select(x => x.GetMessage()))}");
            }
        }

        protected MetadataReference[] GetAdditionalReferences()
        {
            return new MetadataReference[]
            {
                MetadataReference.CreateFromFile(typeof(System.ComponentModel.Composition.ExportAttribute).Assembly.Location)
            };
        }
    }
}


```

