Loader.cs
```
        Solution m_CurrentSolution;

        Loader(string slnFile)
        {
            var workspace = MSBuildWorkspace.Create();
            workspace.LoadMetadataForReferencedProjects = true;
            m_CurrentSolution = workspace.OpenSolutionAsync(slnFile).Result;
        }

        public static void Main(String[] args)
        {
            var root = args[0];
            var loader = new Loader(root);
            var newSolution = loader.DoStuff();
            loader.PrintResult(newSolution);
        }

        public Solution DoStuff()
        {
            var solution = m_CurrentSolution;
            while (QuickWalkthrough(0, ref solution)) { }
            return solution;
        }

        static bool QuickWalkthrough(int iteration, ref Solution solution)
        {
            foreach (var project in solution.Projects)
            {
                foreach (var document in project.Documents)
                {
                    var Model = document.GetSemanticModelAsync().Result;
                    var root = document.GetSyntaxRootAsync().Result;
                    var enumDeclarations = root
                        .DescendantNodes()
                        .OfType<EnumDeclarationSyntax>()
                        .Where(syntax => !syntax.Identifier.ValueText.Contains("Explode" + iteration));

                    if (enumDeclarations.Any())
                    {
                        var type = enumDeclarations.First();
                        var typeSymbol = Model.GetDeclaredSymbol(type);
                        var newName = "Explode" + iteration + typeSymbol.Name;
                        solution = Renamer.RenameSymbolAsync(solution, typeSymbol, newName, solution.Options).Result;
                        return true;
                    }
                }
            }

            return false;
        }

        void PrintResult(Solution solution)
        {
            foreach (var syntaxTree in solution.Projects.SelectMany(p => p.Documents))
            {
                var newContent = syntaxTree.GetSyntaxTreeAsync().Result.GetText();
                Console.Out.WriteLine(newContent);
            }
        }
```

