public class AuthContextResolver : IFieldMiddleware
    {
        public async Task<object> Resolve(IResolveFieldContext context, FieldMiddlewareDelegate next)
        {
            if (context.Parent == null)
            {
                VisitFields(context.ParentType, context.FieldAst, (fieldType, field) =>
                {
                    // you can access fieldType.Metadata here
                    return true;
                });
            }
            return await next(context);
        }

        private void VisitFields(IObjectGraphType objectType, Field field, Func<FieldType, Field, bool> visitor)
        {
            var fieldType = objectType.Fields.First(type => type.Name == field.Name);
            if (!visitor(fieldType, field))
            {
                return;
            }
            foreach (var selection in field.SelectionSet.Selections)
            {
                if (selection is Field && fieldType.ResolvedType is IObjectGraphType)
                {
                    VisitFields((IObjectGraphType) fieldType.ResolvedType, (Field) selection, visitor);
                }
            }
        }
    }
```

