        logger.error("!!!!!!! DROP {}", s);
        throw new RuntimeException("drop");
    })
    .observeOn(Schedulers.single()) // switch to single thread
    .doOnNext(s -> logger.trace("next single: {}", s)) // item is missing here
    .parallel()
    .runOn(Schedulers.io())
    .map(s -> { // run in parallel
        logger.info("Processing {}", s);
        Thread.sleep(50);
        logger.warn("Processing completed, reprocessing: {}", s);
