        private final Set<UUID> ids;

        public TagEntry(String key, String value, Set<UUID> ids) {
            this.key = key;
            this.value = value;
            this.ids = ids;
        }

        public String getKey() {
            return key;
        }

        public Set<UUID> getIds() {
            return ids;
        }

        @Override
        public int hashCode() {
            return Objects.hash(key, value, ids);
        }

        @Override
        public boolean equals(Object obj) {
            if (this == obj)
                return true;
            if (obj == null)
                return false;
            if (getClass() != obj.getClass())
                return false;
            TagEntry that = (TagEntry) obj;
            return Objects.equals(this.key, that.key) &&//
                    Objects.equals(this.value, that.value) &&//
                    Objects.equals(this.ids, that.ids);
        }

        @Override
        public String toString() {
            return key + "=" + value + ": " + ids;
        }
    }

    @Test
    void test() {
        UUID a = UUID.randomUUID();
        UUID b = UUID.randomUUID();

        HashSet<TagEntry> expected = new HashSet<>();
        expected.add(new TagEntry("hello", null, Collections.singleton(b)));
        expected.add(new TagEntry("hello", "world", Collections.singleton(a)));

        HashSet<TagEntry> actual = addUntagged(Flowable.just(new TagEntry("hello", "world", Collections.singleton(a))),
                Flowable.just(a, b)).collectInto(new HashSet<TagEntry>(), HashSet::add)
                    .blockingGet();

        assertEquals(expected, actual);
    }

    private Flowable<TagEntry> addUntagged(Flowable<TagEntry> tags, Flowable<UUID> allIds) {
        Single<List<UUID>> allRootIds = allIds.toList()
            .cache();

        return tags.publish(pubTags -> {
            Single<Map<String, Set<UUID>>> taggedRootIdsByDim = pubTags.collect(TreeMap::new, (map, entry) -> {
                map.computeIfAbsent(entry.getKey(), (k) -> new TreeSet<>())
                    .addAll(entry.getIds());
            });

            Flowable<TagEntry> untags = taggedRootIdsByDim.zipWith(allRootIds, this::generateUntags)
                .flattenAsFlowable(x -> x);

            return pubTags.concatWith(untags); // only return pubTags
            // return untags.concatWith(pubTags); // only return untags
        });
    }

    private List<TagEntry> generateUntags(Map<String, Set<UUID>> taggedRootIdsByDim, List<UUID> allRootIds) {
        return taggedRootIdsByDim.entrySet()
            .stream()
            .map((entry) -> {
                // substract the two sets to find root ids that are don't have this tag
                TreeSet<UUID> untagged = new TreeSet<>(allRootIds);
                untagged.removeAll(entry.getValue());

                return new TagEntry(entry.getKey(), null, untagged);
            })
            .collect(Collectors.toList());
    }
}
