    .subscribe {
        // this is not called when terminationSignal terminates earlier
    }

observable.takeUntil(terminationSignal)
    .subscribe {
        // this is also not called when terminationSignal terminates before any emission
    }

completable.takeUntil(terminationSignal)
    .subscribe {
        // this is called even when terminationSignal terminates earlier
    }
