  private static final int NUM_EVENTS = 200;

  public static void main(String[] args) throws Exception {
    System.out.println("Executing blocking operation...");
    observe(() -> Thread.sleep((long) (Math.random() * 100)));

    System.out.println("Executing non-blocking operation...");
    observe(() -> {});
  }

