    AtomicInteger messageReceived = new AtomicInteger(0);
    AtomicInteger observableComplete = new AtomicInteger(0);
    ConcurrentHashMap<String, AtomicInteger> counters = new ConcurrentHashMap<>();

    createObservable()
        .compose(createThreadPoolTransformer())
        .compose(performOperation(op))
        .subscribe(
