              counters.compute(Thread.currentThread().getName(), (key, ai) -> {
                ai = ai == null ? new AtomicInteger(0) : ai;
                ai.incrementAndGet();
                return ai;
              });
              messageReceived.incrementAndGet();

              if (messageReceived.get() == NUM_EVENTS) {
                System.out.println(String.format("Counts per thread: %s \n", counters));
              }
            }, e -> {}, observableComplete::incrementAndGet);

    while(observableComplete.get() == 0) {
      // wait until it's complete
    }
  }

  private static Observable<Long> createObservable() {
    return Observable.just(0).repeat(NUM_EVENTS).map(al -> {
      Thread.sleep(10);
      return System.currentTimeMillis();
    });
  }

  private static ObservableTransformer<Long, Long> createThreadPoolTransformer() {
    AtomicLong counter = new AtomicLong(0);
    return upstream ->
        upstream
            .groupBy(e -> counter.getAndIncrement() % NUM_THREADS)
            .flatMap(g -> g.observeOn(Schedulers.io()));
  }

  private static ObservableTransformer<Long, Long> performOperation(Action op) {
    return upstream -> upstream
        .map(
