@Test public void testUnsubscribe() throws InterruptedException {
    PublishSubject<Boolean> mBooleanPublishSubject = PublishSubject.create();
    MutableObject<Subscription> subscription = new MutableObject<>();
    subscription.set(mBooleanPublishSubject
            .doOnNext(value -> System.out.println("Sending value " + value))
            .onBackpressureBuffer()
            .observeOn(Schedulers.computation())
            .map(v -> {
                System.out.println("map: started");
                long sum = 0;
                for (int i = 0; i < 10000000; i++) {
                    sum += i;
                }
                System.out.println("map: ended");
                return sum > 0;
            })
            .lift(new BreakIfUnsubscribed())
            .doOnNext(__ -> System.out.println("Before subscribe"))
            .lift(new BreakIfUnsubscribed())
            .subscribe(value -> System.out.println("Value received " + value)));

    mBooleanPublishSubject.onNext(true);
    mBooleanPublishSubject.onNext(true);
    Thread.sleep(30);
    System.out.println("Unsubscribing");
    subscription.get().unsubscribe();
    System.out.println("Unsubscribed");
    Thread.sleep(1000);
}

public class BreakIfUnsubscribed<T> implements Observable.Operator<T, T> {
    public BreakIfUnsubscribed() {
    }

    @Override public Subscriber<? super T> call(Subscriber<? super T> subscriber) {
        return new Subscriber<T>(subscriber) {
            @Override
            public void onCompleted() {
                if (!subscriber.isUnsubscribed()) {
                    subscriber.onCompleted();
                }
            }

            @Override
            public void onError(Throwable t) {
                if (!subscriber.isUnsubscribed()) {
                    subscriber.onError(t);
                }
            }

            @Override
            public void onNext(T item) {
                if (!subscriber.isUnsubscribed()) {
                    subscriber.onNext(item);
                }
            }
        };
    }
}
