        }
        boolean listFlag = false;
        Sheet sheet = cell.getSheet();
        // æ­¤å¤„ä¸ºExcelUrlConverterUtilçš„è¿”å›å€¼
        List<ImageData> imageDataList = cellDataList.get(0).getImageDataList();
        if (CollectionUtil.isNotEmpty(imageDataList)) {
