public static <T> void exportSheet(HttpServletResponse response, String fileName, List<String> sheetName, List<List<T>> list, Class<T> clazz) {
		response.setContentType("application/vnd.ms-excel");
		response.setCharacterEncoding(Charsets.UTF_8.name());
		ServletOutputStream outputStream = null;
		ExcelWriter excelWriter = null;
		try {
			fileName = URLEncoder.encode(fileName, Charsets.UTF_8.name());
			response.setHeader("Content-disposition", "attachment;filename=" + fileName + ".xlsx");
			outputStream = response.getOutputStream();
			excelWriter = EasyExcel.write(outputStream, clazz).build();

			int i = 0;
			int k = 0;
			for (List<T> c : list) {
				WriteSheet writeSheet = EasyExcel.writerSheet(i++, sheetName.get(k++)).head(clazz).build();
				excelWriter.write(c, writeSheet);
			}
			excelWriter.finish();
			outputStream.flush();
		} catch (UnsupportedEncodingException e) {
			throw new RuntimeException(e);
		} catch (IOException e) {
			throw new RuntimeException(e);
		} finally {
			if (outputStream != null) {
				try {
					outputStream.close();
				} catch (IOException e) {
					throw new RuntimeException(e);
				}
			}
		}
	}
