public class CustomCellWriteHandler implements CellWriteHandler {
    private WriteCellStyle commonCellStyle; // é€šç”¨æ ·å¼

    public CustomCellWriteHandler(WriteCellStyle commonCellStyle) {
        this.commonCellStyle = commonCellStyle;
    }

    @Override
    public void afterCellDispose(CellWriteHandlerContext context) {
        int rowIndex = context.getRowIndex();
        int columnIndex = context.getColumnIndex();

        if (rowIndex == 11 && columnIndex == 2) {
            WriteCellData<?> writeCellData = context.getFirstCellData();
            WriteCellStyle customCellStyle = new WriteCellStyle();
            WriteFont customFont = new WriteFont();
            customFont.setFontName("Times New Roman");
            customFont.setFontHeightInPoints((short) 10);
            customFont.setItalic(Boolean.TRUE);
            customCellStyle.setWriteFont(customFont);
            WriteCellStyle.merge(customCellStyle, writeCellData.getOrCreateStyle());
        } else {
            WriteCellData<?> writeCellData = context.getFirstCellData();
            WriteCellStyle cellStyle = writeCellData.getOrCreateStyle();
            WriteCellStyle.merge(commonCellStyle, cellStyle);
        }
    }
}

