    public void generatorTotalFile(List<String> generatorFilePath) {
        if (generatorFilePath != null && generatorFilePath.size() > 0) {
            String totalFilePath = SystemPropertyUtils.getPrintFileStorePath() + File.separator + "total-" + SystemPropertyUtils.getCurrentTime() +".xlsx";
            File totalFile = new File(totalFilePath);
            int i = 0;
            ExcelWriter excelWriter = null;
            try {
                excelWriter = EasyExcel.write(totalFile).excelType(ExcelTypeEnum.XLSX).build();

                for (String filePath : generatorFilePath) {
                    WriteSheet writeSheet = EasyExcel.writerSheet(i, "sheet" + (i + 1)).build();
                    EasyExcel.read(filePath, new MergeListener(excelWriter, writeSheet)).sheet().doRead();
                    i++;
                }
            } catch (Exception e) {
                log.error(e.getMessage(), e);
            } finally {
                if (excelWriter !=null) {
                    excelWriter.finish();
                }
            }
        }
    }
