public class PersonIdConverter implements Converter<Integer> {
	@Autowired
	private  THrPersonService tHrPersonService;
	@Override
	public Class supportJavaTypeKey() {
		return Integer.class;
	}

	@Override
	public CellDataTypeEnum supportExcelTypeKey() {
		return CellDataTypeEnum.STRING;
	}

	@Override
	public Integer convertToJavaData(CellData cellData, ExcelContentProperty excelContentProperty, GlobalConfiguration globalConfiguration) throws Exception {
		String stringValue = cellData.getStringValue();
		Integer personId = tHrPersonService.getOne(Wrappers.<THrPerson>query().lambda().eq(THrPerson::getName,stringValue)).getPersonId();
		return personId;
	}

	@Override
	public CellData convertToExcelData(Integer integer, ExcelContentProperty excelContentProperty, GlobalConfiguration globalConfiguration) throws Exception {
		String personName= tHrPersonService.getOne(Wrappers.<THrPerson>query().lambda().eq(THrPerson::getPersonId,integer)).getName();
		return new CellData(personName);
	}
}
