   public String uploadToOssCfExcel(List list, Class cls, String fileName) throws Exception{
        try (ByteArrayOutputStream outputStream = new ByteArrayOutputStream()) {
            ExcelWriterBuilder writerBuilder = EasyExcelFactory.write(outputStream, cls);
            ExcelWriter excelWriter = EasyExcel.write(outputStream, cls).build();
            if (CollectionUtils.isEmpty(list)){
                ExcelWriterSheetBuilder sheetWriter = writerBuilder.excelType(ExcelTypeEnum.XLSX).sheet(0);
                sheetWriter.doWrite(list);
            }else {
                int count = (list.size() % SHEET_SIZE == 0) ? (list.size() / SHEET_SIZE) : ((list.size() / SHEET_SIZE) + 1);
                for (int i = 1; i <= count; i++) {
                    WriteSheet writeSheet = EasyExcel.writerSheet(i-1).build();
                    if (i * SHEET_SIZE > list.size()) {
                        excelWriter.write(list.subList((i - 1) * SHEET_SIZE, list.size()), writeSheet);
                        break;
                    } else {
                        excelWriter.write(list.subList((i - 1) * SHEET_SIZE, i * SHEET_SIZE), writeSheet);
                    }
                }
                excelWriter.finish();
            }
            String failureUrl = uploadToOss(BucketFileDirEnum.BASE.getFileDir()  + fileName, outputStream);
            log.info("æ–‡ä»¶ä¸Šä¼ æˆåŠŸï¼Œurl={}", failureUrl);
            return failureUrl;
        }catch (Exception e){
            log.info("æ–‡ä»¶ä¸Šä¼ å¼‚å¸¸", e);
            throw e;
        }
