private CellStyle getMoneyCellStyle(Cell cell){
    if(null == moneyCellStyle) {
        moneyCellStyle = cell.getSheet().getWorkbook().createCellStyle();
        DataFormat df = cell.getSheet().getWorkbook().createDataFormat(); // æ­¤å¤„è®¾ç½®æ•°æ®æ ¼å¼
        moneyCellStyle.setDataFormat(df.getFormat("#,##0.00"));//æ•°æ®æ ¼å¼åªæ˜¾ç¤ºæ•´æ•°
        moneyCellStyle.setBorderTop(BorderStyle.THIN);
        moneyCellStyle.setBorderLeft(BorderStyle.THIN);
        moneyCellStyle.setBorderRight(BorderStyle.THIN);
        moneyCellStyle.setBorderBottom(BorderStyle.THIN);
    }
    return moneyCellStyle;
}

