			HlsMediaPlaylist.Segment segment = hlsMediaPlaylist.segments.get( iSegment );
			int iNumParts = ( segment != null && segment.parts != null ) ? segment.parts.size() : 0;
			Log.d( "AESHlsPlaylistParser", "AesHlsPlaylistParser : parse : segment.url = " + segment.initializationSegment.fullSegmentEncryptionKeyUri + " | segment.initializationSegment.fullSegmentEncryptionKeyUri = " + segment.fullSegmentEncryptionKeyUri + " | iNumParts = " + iNumParts );

			// Change the URI
			segment.initializationSegment.fullSegmentEncryptionKeyUri = newURI;
			segment.fullSegmentEncryptionKeyUri = newURI;

			// Parts
			for( int iPart = 0; iPart < iNumParts; ++iPart )
