    private void initializePlayer () {
        String link = getIntent().getExtras().getString("anime_link");
        Uri myUri = Uri.parse(link);

        DefaultDrmSessionManager<FrameworkMediaCrypto> drmSessionManager = null;
        if (getIntent().hasExtra(DRM_SCHEME_EXTRA) || getIntent().hasExtra(DRM_SCHEME_UUID_EXTRA)) {
            String drmLicenseUrl = getIntent().getStringExtra(DRM_LICENSE_URL_EXTRA);
            String[] keyRequestPropertiesArray =
                    getIntent().getStringArrayExtra(DRM_KEY_REQUEST_PROPERTIES_EXTRA);
            boolean multiSession = getIntent().getBooleanExtra(DRM_MULTI_SESSION_EXTRA, false);
