  }

  @Override public boolean canAcquireSession(DrmInitData drmInitData) {
    if (managers == null) return false;
    accepted = new Pair<>(null, null);
    for (DrmSessionManager<T> manager : managers) {
      if (manager.canAcquireSession(drmInitData)) {
        accepted = Pair.create(drmInitData, manager);
        break;
      }
    }

    return accepted.second != null;
  }

  @Override public DrmSession<T> acquireSession(Looper playbackLooper, DrmInitData drmInitData) {
    return accepted.second != null ? accepted.second.acquireSession(playbackLooper, drmInitData) : null;
