public class DecryptingInterceptor implements Interceptor {

    private final DecryptionStrategy decryptionStrategy;

    public interface DecryptionStrategy {
        byte[] decrypt(InputStream stream) throws Exception;
    }

    public DecryptingInterceptor(DecryptionStrategy decryptionStrategy) {
        this.decryptionStrategy = decryptionStrategy;
    }

    @Override
    public Response intercept(Chain chain) throws IOException {
        Response response = chain.proceed(chain.request());
        if (response.isSuccessful()) {
            Response.Builder newResponse = response.newBuilder();
            String contentType = response.header("Content-Type");
            if (TextUtils.isEmpty(contentType)) contentType = "application/json";
            InputStream cryptedStream = response.body().byteStream();
