```typescript
import { Request, Response, NextFunction } from 'express';
import axios from 'axios';
import { UnauthroizedError } from '../errors/HttpErrors';

export const attachUserHeaderMiddleware = async (
  req: Request,
  res: Response,
  next: NextFunction
) => {
  const id = axios.interceptors.request.use(
    (config) => {
      if (!req.auth) {
        throw new UnauthorizedError();
      }

      if (config.url?.startsWith("/server")) {
        config.headers['user-id'] = req.auth.user.id;
      }

      return config;
