    const {data, info} = imageData;
    return {
        width: info.width,
        height: info.height,
        data: new Uint8ClampedArray(data.buffer),
    };
}

type ImageDataLike = {
    data:   Uint8ClampedArray
    width:  number
    height: number
};
type ImageDataLikeEx = {
    data:   Uint8ClampedArray | Uint8Array
    width:  number
    height: number
    channels?: 4 | 1 | 2 | 3
};
