THREE.MyGeometry = function ( params, phi_range, phi_res, theta_range, theta_res ) {

    THREE.Geometry.call( this );

    var scope = this;

   // here some for the issue irrelevant stuff was commented out
   this.params = default_settings;

    this.phi_range = phi_range || 180.0;
    this.phi_res = phi_res || 180.0;

