            cos_phi = Math.cos(phi),
            sin_phi = Math.sin(phi),
            radius_theta = radius(theta, params...),
            radius_phi = radius(phi, params...),
            x = radius_theta * cos_theta * radius_phi * cos_phi,
            y = radius_theta * sin_theta * radius_phi * cos_phi,
            z = radius_phi * sin_phi;

        return new THREE.Vector3(x, y, z).multiplyScalar(100);
    }

    var phi_step_size = this.phi_range / this.phi_res;
    var theta_step_size = this.theta_range / this.theta_res;

    for (var phi_i = 0; phi_i < this.phi_res; phi_i++) {
