    if (walking) {

        if (t > 30) t = 0;

        if (skin) {

            for ( var i = 0; i < skin.morphTargetInfluences.length; i++) {

                skin.morphTargetInfluences[i] = 0;

            }

            skin.morphTargetInfluences[Math.floor(t)] = 1;
            t += 0.5;

        }

    }

    render();
    stats.update();
