```ts
function WithTest<M, DF>(Cp: React.ComponentType<M & WithProps> & { defaultProps : DF}) : { // Explicit return annotation not required, just for clarity 
  (props: Merge<M, typeof Cp.defaultProps> & InjectProps) : JSX.Element
  defaultProps : DF
} {
  function wrapped (props: Merge<M, typeof Cp.defaultProps> & InjectProps) {
    let params = omit(props, ['ij']) as M
    let wp = (props.ij * 100) + '%'
    return <Cp {...params as any} wp={wp}/> // The conditional types that handle the defaults make this assertion necessary unfortunately 
  }
  wrapped.defaultProps = Cp.defaultProps
  return wrapped
}
