function isJasmineExpression(checker: Checker, node: ts.CallExpression): boolean {
  // get the `jasmine` module file path
  const jasmineNode = checker.typeChecker
    .getAmbientModules()
    .find(x => x.name.replace(/"/g, '') === 'jasmine');

  if (!jasmineModule) {
