``` js
// bundle.js
define('alert/alert', [
    'require',
    'exports'
], function (require, exports) {
    var Alert = function () {
        function Alert() {
        }
        Alert.prototype.alert = function (message) {
            alert(message);
        };
        return Alert;
    }();
    exports.Alert = Alert;
});
define('log/log', [
    'require',
    'exports'
], function (require, exports) {
    var Log = function () {
        function Log() {
        }
        Log.prototype.log = function (message) {
            console.log(message);
        };
        return Log;
    }();
