function mergeLists() {
    // Make "arguments" usable
    var args = []
    for (var i = 0; i < arguments.length; ++i) args[i] = arguments[i]

    var queuedItems = {} // Items that are qued to be tested
    var indexes = {} // Indexes of items that are in the queu

    // Set initial indexes for all the items in the args
    for (var i in args) {
        indexes[i] = 0
    }

    // Set the default ques
    for (var i in indexes) {
        queuedItems[i] = args[i][0]
    }

    function nextItem() {
        var n = Object.keys(indexes)[0] // Set a default newest item

        // If there are no more keys, then break
        if (n == null) {
            return null
        }

        // Find the least of the qued values
        for (var i in queuedItems) {
            var nValue = args[n][indexes[n]] // Value of current record item
            var iValue = args[i][indexes[i]] // Value of item being tested

            if (n && nValue && iValue && iValue.time < nValue.time ) {
                n = i // If the tested item is newer than the old recore, set the new record
            }
        }

        // Store a return value
        var returnValue = [n, args[n][indexes[n]]][1]

        indexes[n]++ // Incrememnt by one
        queuedItems[n] = args[n][indexes[n]] // Set value to the new index

        // Delete keys if that list is done with
        if (queuedItems[n] == null) {
            delete indexes[n]
            delete queuedItems[i]
        }

        return returnValue
    }

    // Make a list of the items
    var sorted = []
    while (sorted.length == 0 || sorted[sorted.length - 1] != null) { // If the list has not been started yet or the last item is not a value
        sorted.push(nextItem()) // Add the next item to the array
    }

    sorted.pop() // Remove the last item off the list, since it is null

    return sorted
}

