```javascript
// utility to calculate ar
const getImageAspectRatio = (blob: Blob): Promise<number> => {
  return new Promise((resolve, reject) => {
    const img = new Image();
    const url = URL.createObjectURL(blob); // Create object URL

    img.onload = () => {
      const aspectRatio = img.width / img.height;
      resolve(aspectRatio);
      URL.revokeObjectURL(url); // Release the object URL
    };

    img.onerror = () => {
      reject(new Error("Failed to load image"));
      URL.revokeObjectURL(url); // Release the object URL on error
    };

    img.src = url;
  });
};
