Continuous feedback convo

```js
import { Scraper } from "agent-twitter-client";
import fs from "fs";
import chalk from "chalk";
import * as readline from "readline/promises";

let scraper = null;

async function initializeScraper() {
  try {
    scraper = new Scraper();
    console.log("Scraper initialized successfully");

    console.log("Loading cookies from file...");
    const cookiesJson = JSON.parse(fs.readFileSync("./cookies.json", "utf-8"));
    console.log(`Found ${cookiesJson.length} cookies`);

    if (cookiesJson.length === 0) {
      console.log("No cookies found, skipping login");
      return;
    }

    // Format cookies as strings in the Set-Cookie header format
    const formattedCookies = cookiesJson.map((cookie) => {
      let cookieString = `${cookie.key}=${cookie.value}`;
      cookieString += `; Domain=${cookie.domain}`;
      cookieString += `; Path=${cookie.path}`;
      if (cookie.expires) cookieString += `; Expires=${cookie.expires}`;
      if (cookie.secure) cookieString += "; Secure";
      if (cookie.httpOnly) cookieString += "; HttpOnly";
      if (cookie.sameSite) cookieString += `; SameSite=${cookie.sameSite}`;
      return cookieString;
    });

    console.log("Setting cookies in scraper...");
    await scraper.setCookies(formattedCookies);

    // Check if we're actually logged in
    const isLoggedIn = await scraper.isLoggedIn();
    console.log("Login check result:", isLoggedIn);

    return scraper;
  } catch (error) {
    console.error("Failed to initialize Twitter scraper:", error);
    throw error;
  }
}

async function main() {
  await initializeScraper();

  try {
    const rl = readline.createInterface({
      input: process.stdin,
      output: process.stdout,
    });

    const messages = [];
    let conversationId;

    console.log("Chat with Grok! (Type 'exit' to quit)");

    const askQuestion = async () => {
      const input = await rl.question(chalk.green("You: "));

      if (input.toLowerCase() === "exit") {
        rl.close();
        return false;
      }

      messages.push({ role: "user", content: input });

      console.log("\n" + chalk.yellow("Thinking...") + "\n");

      const response = await scraper.grokChat({
        conversationId,
        messages,
      });

      conversationId = response.conversationId;

      if (response.rateLimit?.isRateLimited) {
        console.log(chalk.red("Rate Limited: ") + response.rateLimit.message);
        if (response.rateLimit.upsellInfo) {
          console.log(chalk.yellow("\nLimit Info:"));
          console.log(`Usage Limit: ${response.rateLimit.upsellInfo.usageLimit}`);
          console.log(`Duration: ${response.rateLimit.upsellInfo.quotaDuration}`);
          console.log(`${response.rateLimit.upsellInfo.title}`);
          console.log(`${response.rateLimit.upsellInfo.message}`);
        }
        rl.close();
        return false;
      } else {
        messages.push({ role: "assistant", content: response.message });
        console.log(chalk.blue("Grok: ") + response.message + "\n");
      }

      console.log(chalk.gray("-------------------------------------------") + "\n");

      return true;
    };

    while (await askQuestion()) {}
  } catch (error) {
    console.error(chalk.red("Failed to send Grok message:"), error);
  }
}

main();
```


