<script>
    // 替换为你的实际备案信息
    const icpNumber = "京ICP备12345678号";
    const gonganNumber = "京公网安备 11000002000001号";
    const domainName = window.location.hostname;

    // 创建备案信息元素
    const footer = document.createElement("div");
    footer.style.width = "100%";
    footer.style.textAlign = "center";
    footer.style.padding = "10px 0";
    footer.style.position = "relative";
    footer.style.bottom = "0";
    footer.style.marginTop = "20px";

    const icpElement = document.createElement("span");
    icpElement.textContent = `ICP备案号：${icpNumber} |`;

    const gonganElement = document.createElement("span");
    gonganElement.textContent = ` 网安备案号：${gonganNumber}`;

    // 将备案信息添加到footer
    footer.appendChild(icpElement);
    footer.appendChild(gonganElement);

    // 将footer添加到body
    document.body.appendChild(footer);
</script>
